mapper.select:
{
       call xp_prd_sProcessMain(#{process, javaType=String, jdbcType=VARCHAR, mode=IN}
           , #{articleTypeID, javaType=String, jdbcType=VARCHAR, mode=IN}
           , #{useYN, javaType=String, jdbcType=CHAR, mode=IN})
       }

{
       call xp_prd_sProcessSub(#{processID, javaType=String, jdbcType=VARCHAR, mode=IN}
           ,#{chkArticleGrpID, javaType=int, jdbcType=SMALLINT, mode=IN}
           ,#{articleGrpID, javaType=String, jdbcType=VARCHAR, mode=IN}
           ,#{useClss, javaType=String, jdbcType=VARCHAR, mode=IN}
            )}

{
       call xp_prd_iProcess(
            #{processID		 , javaType=String, jdbcType=VARCHAR, mode=IN}
           , #{process			 , javaType=String, jdbcType=VARCHAR, mode=IN}
           , #{eProcess		 , javaType=String, jdbcType=VARCHAR, mode=IN}
           , #{fProcess		 , javaType=String, jdbcType=VARCHAR, mode=IN}
           , #{articleTypeID	 , javaType=String, jdbcType=VARCHAR, mode=IN}
           , #{detailWorkYN	 , javaType=String, jdbcType=CHAR, mode=IN}
           , #{sortSeq			 , javaType=int, jdbcType=SMALLINT, mode=IN}
           , #{useYN			 , javaType=String, jdbcType=CHAR, mode=IN}
           , #{childCheckYN	 , javaType=String, jdbcType=CHAR, mode=IN}
           , #{createuserID	 , javaType=String, jdbcType=VARCHAR, mode=IN}
            )
       }

{
       call xp_prd_uProcess(
           , #{processID, javaType=String, jdbcType=VARCHAR, mode=IN}
           , #{process			    , javaType=String, jdbcType=VARCHAR, mode=IN}
           , #{eProcess		    , javaType=String, jdbcType=VARCHAR, mode=IN}
           , #{fProcess		    , javaType=String, jdbcType=VARCHAR, mode=IN}
           , #{articleTypeID	    , javaType=String, jdbcType=VARCHAR, mode=IN}
           , #{detailWorkYN	    , javaType=String, jdbcType=CHAR, mode=IN}
           , #{sortSeq			    , javaType=int,    jdbcType=SMALLINT, mode=IN}
           , #{useYN			    , javaType=String, jdbcType=CHAR, mode=IN}
           , #{childCheckYN	    , javaType=String, jdbcType=CHAR, mode=IN}
           , #{lastUpdateUserID	, javaType=String, jdbcType=VARCHAR, mode=IN}
            )
       }

{
       call xp_prd_dProcess(#{processID, javaType=String, jdbcType=VARCHAR, mode=IN}
           , #{lastUpdateUserID, javaType=String, jdbcType=VARCHAR, mode=IN})
       }
